import React from 'react'
import PropTypes from 'prop-types'
import Fab from '@material-ui/core/Fab'
import { 
  withStyles, 
  createMuiTheme, 
  MuiThemeProvider } from '@material-ui/core/styles'

const theme = createMuiTheme({
palette: {
        common: {
            black:'#000',
            white:'#fff'
        }
        ,background:{
            paper:'#fff',
            default:'#fafafa'
        }
        ,primary:{
            light:'#7986cb',
            main:'rgba(35, 99, 241, 1)',
            dark:'#303f9f',
            contrastText:'#fff'
        }
        ,secondary:{
            light:'#ff4081',
            main:'rgba(221, 7, 44, 0.78)',
            dark:'#c51162',
            contrastText:'#fff'
        }
        ,error:{
            light:'#e57373',
            main:'#f44336',
            dark:'#d32f2f',
            contrastText:'#fff'
        }
        ,text:{
            primary:'rgba(0, 0, 0, 0.87)',
            secondary:'rgba(0, 0, 0, 0.54)',
            disabled:'rgba(0, 0, 0, 0.38)',
            hint:'rgba(0, 0, 0, 0.38)'
        }
    }

});

const styles = theme => ({
  fab: {
    margin: theme.spacing.unit,
    padding: 5,
    width: 200,
    height: 100,
    text: 300,
    boxshadow: 4,
  },
  input: {
    display: 'none',
  },
});


function Player(props) {
/*export const Player = props => {*/
  const { classes } = props;
  return (

    <div className={classes.palette}>
     <MuiThemeProvider theme={theme}>
    
      <h1>{'Choose Player One'}</h1>
      <div className="options">
        <Fab 
        onClick={props.onClick} 
        variant="contained" 
          style={
            { 
              marginRight:'50px',
              width: 150,
              height: 150,
              fontSize: '74px', 
              color:'secondary',
          }} 
          color="light"  
          margin="theme.spacing.unit" 
          className="fab"
          value="AI"
          >
          {"X"}
          </Fab>
        <Fab 
        onClick={props.onClick} 
        variant="contained" 
          style={
            { 
              marginRight:'50px',
              width: 150,
              height: 150,
              color: "secondary",
              fontSize: '74px',
          }} 
          color="light"  
          margin="theme.spacing.unit" 
          className="fab"
          value="AI"
          >

          {"O"}
          </Fab>
      </div>
       </MuiThemeProvider>
    </div>
  )
}



Player.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Player);
